1，导航组件&lt;!-- components/my-component/index.wxml --&gt;&lt;view class=&quot;topbar&quot; style=&quot;background: &#123;&#123;imgdata.bgcolor&#125;&#125;;padding-top:&#123;&#123; statusBarHeight &#125;&#125;px ;&quot;&gt;  &lt;view class=&quot;tb_body&quot;&gt;    &lt;view class=&quot;set row&quot; wx:if=&quot;&#123;&#123;setting&#125;&#125;&quot; bindtap=&quot;setClick&quot;&gt;&#123;&#123; setting &#125;&#125;&lt;/view&gt;    &lt;image wx:if=&quot;&#123;&#123;back&#125;&#125;&quot; bindtap=&quot;callback&quot; src=&quot;&#123;&#123;back&#125;&#125;&quot; style=&quot;width:48rpx;height:48rpx;margin-left: 30rpx;&quot;&gt;&lt;/image&gt;    &lt;image wx:if=&quot;&#123;&#123;home&#125;&#125;&quot; bindtap=&quot;callhome&quot; src=&quot;&#123;&#123;home&#125;&#125;&quot; style=&quot;width:64rpx;height:64rpx;margin-left: 36rpx;&quot;&gt;&lt;/image&gt;    &lt;view class=&quot;title row&quot; style=&quot;color: &#123;&#123;title.color&#125;&#125;&quot;&gt;&#123;&#123; title.title &#125;&#125;&lt;/view&gt;  &lt;/view&gt;&lt;/view&gt;&lt;!-- &lt;view&gt;  &lt;cover-image class=&quot;&quot; src=&quot;&quot; bindload=&quot;&quot; binderror=&quot;&quot;&gt;     mode=&quot;widthFix&quot;  &lt;/cover-image&gt;&lt;/view&gt; --&gt;&lt;image class=&quot;bg_head&quot; wx:if=&quot;&#123;&#123;imgdata.path&#125;&#125;&quot; src=&quot;&#123;&#123;imgdata.path&#125;&#125;&quot; bindload=&quot;imgInfo&quot; style=&quot;width:&#123;&#123;imgdata.width&#125;&#125;;height:&#123;&#123;imgdata.height&#125;&#125;&quot;&gt;&lt;/image&gt;&lt;view class=&quot;top-comend&quot; style=&quot;padding-top:&#123;&#123; statusBarHeight &#125;&#125;px&quot;&gt;&lt;/view&gt;const app = getApp();Component(&#123;  /**   * 组件的属性列表   */  properties: &#123;    setting: &#123;      type: String,      value: &#x27;&#x27;    &#125;,    title: &#123;      type: Object,      value: &#123;        color: &#x27;#fff&#x27;,        title: &#x27;&#x27;      &#125;    &#125;,    imgdata: &#123;      type: Object,      value: &#123;        path: &#x27;&#x27;,        width: &#x27;&#x27;,        height: &#x27;&#x27;,        bgcolor: &#x27;#fff&#x27;      &#125;    &#125;,    back: &#123;      type: String,      value: &#x27;&#x27;    &#125;,    home: &#123;      type: String,      value: &#x27;&#x27;    &#125;  &#125;,  /**   * 组件的初始数据   */  data: &#123;    statusBarHeight: app.globalData.statusBarHeight,    imgwidth: 0,    imgheight: 0  &#125;,  ready: function () &#123;    // console.log(this.data.imgdata);  &#125;,  /**   * 组件的方法列表   */  methods: &#123;    imageLoad: function (e) &#123;      var _this = this;      var $width = e.detail.width, //获取图片真实宽度        $height = e.detail.height,        ratio = $width / $height; //图片的真实宽高比例      var viewWidth = 500, //设置图片显示宽度，        viewHeight = 500 / ratio; //计算的高度值      this.setData(&#123;        imgwidth: viewWidth,        imgheight: viewHeight      &#125;);    &#125;,    setClick(e) &#123;      this.triggerEvent(&#x27;setBtn&#x27;, true);    &#125;,    callback() &#123;      wx.navigateBack(&#123;        delta: 1      &#125;);    &#125;,    callhome() &#123;      wx.switchTab(&#123;        url: &#x27;/pages/home/index&#x27;      &#125;);    &#125;,    // 背景图回调    imgInfo: function (event) &#123;      // this.setData(&#123;      //   imgWidth:event.detail.width,      //   imgHeight:event.detail.height      // &#125;)    &#125;  &#125;&#125;);/* components/my-component/index.wxss */.topbar &#123;  width: 100%;  height: 88rpx;  /* background-color: pink; */  position: fixed;  top: 0;  left: 0;  z-index: 999;&#125;.tb_body &#123;  height: 100%;  display: flex;  justify-content: space-between;  align-items: center;&#125;.row &#123;  font-size: 32rpx;  font-family: PingFangSC, PingFangSC-Semibold;  font-weight: 600;  color: #ffffff;  line-height: 50rpx;&#125;.set &#123;  font-weight: 400;  margin-left: 60rpx;&#125;.title &#123;  position: absolute;  width: 100%;  /* text-align: center; */  padding-left: 120rpx;  z-index: -1;&#125;.top-comend &#1